options.script:
console.log(data)

function unique(value, index, self) {
  return self.indexOf(value) === index
}

let droneIds = data.series[0].fields[1].values.buffer
let drones = droneIds.filter(unique)
let nDrones = drones.length
var drones_pos_x = []
var drones_pos_y = []
var drones_pos_z = []

for (var i = 0; i < nDrones; i++) {
  let droneId = drones[i];
  var pos_x = []
  var pos_y = []
  var pos_z = []

  for (var j = 0; j < droneIds.length; j++) {
    if (droneIds[j] == droneId) {
      pos_x.push(data.series[0].fields[2].values.buffer[j])
      pos_y.push(data.series[0].fields[3].values.buffer[j])
      pos_z.push(data.series[0].fields[4].values.buffer[j])
    }
  }

  drones_pos_x.push(pos_x)
  drones_pos_y.push(pos_y)
  drones_pos_z.push(pos_z)
}

var traces = []
for (var i = 0; i < nDrones; i++) {
  traces.push({
    x: drones_pos_x[i],
    y: drones_pos_y[i],
    z: drones_pos_z[i],
    type: 'scatter3d',
    mode: 'lines',
    name: `Drone #${drones[i]}`,
    opacity: 1,
    line: {
      width: 6,
    }
  })
}

return {data:traces,layout:{title:'Drone Trajectory'}};
